	#define TVN_MASK (TVN_SIZE - 1)
	#define TVR_MASK (TVR_SIZE - 1)
	#define OFFSET(N) (TVR_SIZE + (N) *TVN_SIZE)
	#define INDEX(V, N) ((V >> (TVR_BITS + (N) *TVN_BITS)) & TVN_MASK)
